package leetcode.strings;

/*
Given two strings s and t, return true if s is a subsequence of t, or false otherwise.

A subsequence of a string is a new string that is formed from the original string by deleting
some (can be none) of the characters without disturbing the relative positions of the remaining characters.
(i.e., "ace" is a subsequence of "abcde" while "aec" is not).
 */
public class IsSubsequence {

    public static void main(String[] args) {
        String s = "leeeeetcode";
        String t = "";

        System.out.println(isSubsequence(s, t));
    }

    public static boolean isSubsequence(String s, String t) {
        int lastEntryIndex;
        int currentEntryIndex;

        if (s.isEmpty()) return true;

        if (!t.contains(s.charAt(0) + "")) {
            return false;
        } else lastEntryIndex = t.indexOf(s.charAt(0));

        for (int i = 1; i < s.length(); i++) {
            currentEntryIndex = t.indexOf(s.charAt(i), lastEntryIndex + 1);
            if (currentEntryIndex > lastEntryIndex) {
                lastEntryIndex = currentEntryIndex;
            } else return false;
        }
        return true;
    }
}

